import React, { useEffect, useState } from 'react'
import { AiFillEyeInvisible } from "react-icons/ai";

function App() {

  const [backendData, setBackendData] = useState([{}]);
  const [passwordHider, setPasswordHider] = useState('');
  const [visible, setVisible] = React.useState(true);

  useEffect(() => {
    fetch("http://localhost:5000/json")
      .then(
        res => res.json())
      .then(
        data => {
          setBackendData(data)
        });
  }, []);


  const hidePassword = (pass) => {
    const passArray = pass.split('');
    for(let i = 0; i < passArray.length; i++){
      passArray[i] = '*';
    }
    const hiddenPasswordShowcase = passArray.join('');
    setPasswordHider(hiddenPasswordShowcase);
    console.log(hiddenPasswordShowcase);
  }

  return (
    <div>
      {
        backendData.map(password => {
          return(
            <div className='entryShower'>
              <p>{password.Titel}</p>
                <div className='passwordField'>
                  <button onClick={() => setVisible(!visible)}> {visible ? '**********' : <p>{password.Password}</p>} </button>
                </div>
            </div>
          )
        })
      }
    </div>
  )
}

export default App
