import React, { useEffect, useState } from 'react'
import { AiFillEyeInvisible } from "react-icons/ai";

function App() {

  const [backendData, setBackendData] = useState([{}]);

  useEffect(() => {
    fetch("http://localhost:5000/json")
      .then(
        res => res.json())
      .then(
        data => {
          setBackendData(data)
        });
    console.log("fetch successful");
  }, []);

  const hidePassword = (pass) => {
    const passArray = pass.split('');
    passArray.array.forEach(element => {
      element = '*';
    });
    console.log(passArray);
  }

  hidePassword("1234");

  return (
    <div>
      {
        backendData.map(password => {
          return(
            <div className='passwordshower'>
              <p>{password.Titel}</p>
                <div className='passwordField'>
                  <button>{password.Password}</button>
                  <AiFillEyeInvisible />
                </div>
            </div>
          )
        })
      }
    </div>
  )
}

export default App
